import {useState,useEffect} from "react";

const initialValues = {
  fName: "",
  cellNumber: ""
};
function Form({
  contacts,
  addContact
}) {


  const [inputText, setInput] = useState(initialValues);

  useEffect(() => {
    setInput(initialValues);
  }, [contacts]);

  function handleChange(event) {
    const {
      value,
      name
    } = event.target;

    setInput(() => {
      return {
        ...inputText,
        [name]: value
      }
    });
  }

  function addItem() {

    if (inputText.fName === "" || inputText.cellNumber === "") {
      return false;
    }
    addContact([...contacts, inputText]);

  }

  return ( <
    div >

    <
    div >
    <
    input onChange = {
      handleChange
    }
    name = "fName"
    placeholder = "First Name"
    value = {
      inputText.fName
    } > < /input> <
    /div>

    <
    div >
    <
    input onChange = {
      handleChange
    }
    name = "cellNumber"
    placeholder = "Phone Number"
    value = {
      inputText.cellNumber
    } > < /input> <
    /div>

    <
    button onClick = {
      addItem
    } > Click here
    for adding < /button>

    <
    /div>
  )
}
export default Form;
