import {useState} from "react";


function Form({contacts,addContact}){

  const initialValues={
    fName:"",
    cellNumber:""
  };
  const [inputText,setInput] = useState(initialValues);

  function handleChange(event){
    const {value,name} = event.target;
    setInput(()=>{
      return {
        ...inputText,
        [name]:value
      }
    });
  }

  function addItem(){
    addContact(()=>{
      return [
        ...contacts,
        inputText
      ]
    })
    console.log(contacts);
    setInput(initialValues);
  }

  return(
    <div>

     <div>
     <input onChange={handleChange} name="fName" placeholder="First Name"></input>
     </div>

      <div>
      <input onChange={handleChange} name="cellNumber" placeholder="Phone Number"></input>
      </div>

      <button onClick={addItem}>Click here for adding</button>

    </div>
  )
}
export default Form;
