import {useState} from "react";


function Form({contacts,addContact}){

  const [inputText,setInput] = useState(" ");

  function handleChange(event){
    const newInput=event.target.value;
    setInput(newInput);
  }

  function addItem(event){
    const {value,name}=event.target;

    if(name==="fName"){
      return [{
        [name]:value,
        ...contacts
      }]
    }

  }

  return(
    <div>

     <div>
     <input onChange={handleChange} name="fName" placeHolder="First Name"></input>
     </div>

      <div>
      <input onChange={handleChange} name="cellNumber" placeHolder="Phone Number"></input>
      </div>

      <button onClick={addItem}>Click here for adding</button>

    </div>
  )
}
export default Form;
