import {useState} from "react";


function List({contacts}){

  const [filterText,setFilterText] = useState(" ");

  const handleFilter = (event) => {
    const newFilter=event.target.value;
    setFilterText(newFilter);
  }

  const filteredArray=contacts.filter((contact)=>{
    return Object.keys(contact).some((key) =>
    contact[key].toString().toLowerCase().includes(filterText.toLowerCase()));
  });

  return(
    <div>
     <ul>
       {
         filteredArray.map((item,index) => <li key={index}>{item.fName}</li>)
       }
     </ul>
     <input name="filterText" placeholder="Search" onChange={handleFilter} value={filterText}></input>
    </div>
  )
}
export default List;
